        scoreCircle.setStroke(Color.WHITE);
        scoreCircle.setStrokeWidth(2);
        scoreCircle.setCenterX(scoreTextX);
        scoreCircle.setCenterY(scoreTextY);
        scoreCircle.setRadius(50);

        group.getChildren().add(scoreCircle);
