    private void startNotes(double[] timings, Stage primaryStage, int score, String selectedSong) {
        Timeline timeline = new Timeline();
        Timeline delayTimeline = new Timeline(new KeyFrame(Duration.seconds(timings[timings.length-1]+5), e -> {
            VBox endLayout = new VBox(20);
            endLayout.setPrefWidth(WIDTH);
            endLayout.setPrefHeight(HEIGHT);
            endLayout.setAlignment(Pos.CENTER);
            endLayout.setStyle("-fx-background-color: #202020;");
            Text letter = new Text("A");
            double accuracy = (totalNotes == 0) ? 100.0 : ((double) successfulHits / totalNotes) * 100;
            if (accuracy == 100.0){
                letter.setText("SSS");
                letter.setFill(Color.PURPLE);
            }
            else if (accuracy >= 97){
                letter.setText("SS");
                letter.setFill(Color.GOLD);
            }
            else if (accuracy >= 95){
                letter.setText("S");
                letter.setFill(Color.SILVER);
            }
            else if (accuracy >= 90){
                letter.setText("A");
                letter.setFill(Color.GREEN);
            }
            else if (accuracy >= 80){
                letter.setText("B");
                letter.setFill(Color.YELLOW);
            }
            else if (accuracy >= 70){
                letter.setText("C");
                letter.setFill(Color.ORANGE);
            }
            else if (accuracy >= 60){
                letter.setText("D");
                letter.setFill(Color.RED);
            }
            else{
                letter.setText("F");
                letter.setFill(Color.DARKRED);
            }
            letter.setFont(new Font(300));

            Text scorekik = new Text("Accuracy: " + accuracyText.getText());
            scorekik.setFill(Color.WHITE);
            scorekik.setFont(new Font(100));

            Text comboscore = new Text("Max Combo: " + maxCombo);
            comboscore.setFill(Color.WHITE);
            comboscore.setFont(new Font(100));


            Button restartButton = new Button("Restart?");
            restartButton.setOnAction(ee -> {
                primaryStage.setScene(menuScene);
                totalNotes = 0;
                successfulHits = 0;
                menuMediaPlayer.play();
                scoreText.setText("0x");
                scoreText.setX(border.getX() + border.getWidth() + 50);
                scoreText.setY(border.getY() + 45);
                accuracyText.setText("100.0%");
                accuracyText.setFill(Color.GREEN);
                failCounter = 12;
                failed = false;
            });

            endLayout.getChildren().addAll(letter,scorekik, comboscore, restartButton);
            endScene = new Scene(endLayout, WIDTH, HEIGHT);
            primaryStage.setScene(endScene);
            String moo = "src/new_best.wav";
            Media woohoo = new Media(new File(moo).toURI().toString());
            MediaPlayer woohoohoo = new MediaPlayer(woohoo);
            woohoohoo.play();

        }));
        delayTimeline.play();
        for (int i = 0; i < timings.length; i++) {
                if (selectedSong == "Gangsta" && i < 34 && gangstayesno == 1){
                    continue;
                }
                KeyFrame keyFrame = new KeyFrame(Duration.seconds(timings[i]), event -> {
                    spawnAndAnimateSquare((Group) gameScene.getRoot(), cursor, scoreText, border, primaryStage, colornum%2);
                    colornum++;
                });
                timeline.getKeyFrames().add(keyFrame);
        }
        timeline.play();

    }
    private void spawnAndAnimateSquare(Group group, Rectangle cursor, Text scoreText, Rectangle border, Stage primaryStage, int col) {
        Rectangle comingsquare = new Rectangle(randomValues[(int) (Math.random() * 3)], randomValues[(int) (Math.random() * 3)], SQUARE_SIZE / 3, SQUARE_SIZE / 3);
        comingsquare.setFill(null);
        if (col == 0){
            comingsquare.setStroke(Color.RED);
        }
        else{
            comingsquare.setStroke(Color.CYAN);
        }
        comingsquare.setStrokeWidth(4);
        comingsquare.setArcWidth(20);
        comingsquare.setArcHeight(20);

        comingsquare.setTranslateZ(10000);
        group.getChildren().add(comingsquare);

        TranslateTransition translateTransition = new TranslateTransition(Duration.seconds(2), comingsquare);
        translateTransition.setFromZ(10000);
        translateTransition.setToZ(0);
        translateTransition.setInterpolator(Interpolator.LINEAR);

        Color startColor = (col == 0) ? Color.RED : Color.CYAN;
        Color endColor = startColor.deriveColor(0, 1, 1, 0); // Fully transparent version of the stroke color
        StrokeTransition strokeTransition = new StrokeTransition(Duration.seconds(2), comingsquare,endColor ,startColor );

        translateTransition.setOnFinished(event -> {
            totalNotes++;
            Bounds comingsquareBounds = comingsquare.getBoundsInParent();
            Bounds cursorBounds = cursor.getBoundsInParent();
            if (!failed){
                if (comingsquareBounds.intersects(cursorBounds)) {
                    score++;
                    if(failCounter < 12){
                        failCounter++;
                    }
                    if (score > maxCombo){
                        maxCombo = score;
                    }
                    successfulHits++;
                    if (scoreText.getText().equals("MISS!")) {
                        scoreText.setX(scoreText.getX() + 15);
                    }
                    int oldScoreLength = String.valueOf(score).length();
                    int newScoreLength = String.valueOf(score).length();
                    if (oldScoreLength == newScoreLength) {
                        scoreText.setText(score + "x");
                    }
                    else if (oldScoreLength < newScoreLength) {
                        scoreText.setX(scoreText.getX() - 5);
                        scoreText.setText(score + "x");
                    }
                    String musicFile = "src/hit.wav"; // Adjust the path to your song files
                    Media sound1 = new Media(new File(musicFile).toURI().toString());
                    MediaPlayer boom = new MediaPlayer(sound1);
                    boom.play();
                }
                else {
                    if (score > maxCombo){
                        maxCombo = score;
                    }
                    failCounter--;
                    String musicFile = "src/miss.wav"; // Adjust the path to your song files
                    Media sound2 = new Media(new File(musicFile).toURI().toString());
                    MediaPlayer bam = new MediaPlayer(sound2);
                    bam.play();
                    score = 0;
                    scoreText.setX(border.getX() + border.getWidth() + 35);
                    scoreText.setText("MISS!");
                }
            }
            if (failCounter == 0){
                failed = true;
                mediaPlayer.stop();
                VBox endLayout1 = new VBox(20);
                endLayout1.setStyle("-fx-background-color: #202020;");
                endLayout1.setPrefWidth(WIDTH);
                endLayout1.setPrefHeight(HEIGHT);
                endLayout1.setAlignment(Pos.CENTER);
                Text letter2 = new Text("YOU FAILED :(");
                letter2.setFont(new Font(200));
                letter2.setFill(Color.RED);
                Text letter3 = new Text("PLEASE RESTART GAME");
                letter3.setFont(new Font(100));
                letter3.setFill(Color.DARKRED);
                endLayout1.getChildren().addAll(letter2, letter3);
                endScene = new Scene(endLayout1, WIDTH, HEIGHT);
                primaryStage.setScene(endScene);
            }
            updateAccuracy();
            group.getChildren().remove(comingsquare);
        });
        strokeTransition.play();

        translateTransition.play();
    }
    private void updateAccuracy() {
        double accuracy = (totalNotes == 0) ? 100.0 : ((double) successfulHits / totalNotes) * 100;
        accuracyText.setText(String.format("%.1f%%", accuracy));
        if (accuracy >= 90.0){
            accuracyText.setFill(Color.GREEN);
        }
        else if(accuracy >= 80.0){
            accuracyText.setFill(Color.YELLOW);
        }
        else if(accuracy >= 70.0){
            accuracyText.setFill(Color.ORANGE);
        }
        else if(accuracy >= 60.0){
            accuracyText.setFill(Color.RED);
        }
        else{
            accuracyText.setFill(Color.DARKRED);
        }
    }

